        e.preventDefault();
       getStory(madlib.noun, madlib.noun2, madlib.adjective, madlib.color)
        setMadlib(initialState);
        setformSubmitted(true);
        console.log(madlib);
    }

    useEffect(() => {
        return () => {
            console.log("unmounted")
        }
    },[formSubmitted])
