        <div className="container">
       {!formSubmitted ? (
                <madlibforminfo noun={madlib.noun} noun2={madlib.noun2} adjective={madlib.adjective} color={madlib.color} handleChange={madlib.handleChange} noun={madlib.noun} />)
    : <button>Reset</button>}   </div > 
    )
}
